INSERT INTO `product` VALUES ('365', 'Móc Khóa Bleach', '6', '64000', '139', '22.jpg', '<p><strong>Bleach </strong>thuộc thể loại truyện tranh viễn tưởng, kể về Kurosaki Ichigo, một thiếu niên có khả năng nhìn thấy các linh hồn, chiến đấu với Hollow và hướng dẫn linh hồn đến thế giới bên kia.</p>\n\n<p>Sản phẩm <strong>Móc Khóa Bleach</strong> được thiết kế dựa trên thanh gươm mà Ichigo sử dụng trong chiến đấu.</p>\n\n<p><strong>Móc Khóa Bleach</strong> được làm từ kim loại cao cấp có độ sáng tự nhiên biến chiếc móc khóa trở thành món đồ trang trí dễ thương bạn có thể đính trên túi, ba lô, điện thoại chứ không chỉ gài trên chùm chìa khóa.</p>', '2017-03-23 00:00:00', '2017-03-23 00:00:00', 'Locndb');
